import redux from "redux";


const initial = {
    counter: 0
};

const reducer = (state = initial, action) => {
    if(action.type === "inc") {
        return {
            counter: state.counter + 1
        };
    }
    if(action.type === "dec") {
        return {
            counter: state.counter - 1
        };
    }
    return state;
};

const store = redux.configureStore(reducer);

module.exports = store;
